const handleSaveToCSV = () => {
    // Get table headers from thead
    const tableHeaders = document.querySelectorAll('#newCustomer thead th');
    const headers = Array.from(tableHeaders).map(th => th.textContent);

    // Get table data from form
    const tableRows = document.querySelectorAll('#newCustomer tbody tr');
    const rowsData = Array.from(tableRows).map(row => {
        const inputs = row.querySelectorAll('input');
        return Array.from(inputs).map(input => input.value);
    });

    // Construct a data object to send
    const dataToSend = {};
    headers.forEach((header, idx) => {
        dataToSend[header] = rowsData.map(row => row[idx]);
    });
    fetch('http://localhost:5000//save-csv', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({cust_id: customerId})
    })
    // Use fetch to send the data to the backend
    fetch('/save-csv', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(dataToSend)
    })
    .then(response => response.json())
    .then(data => {
        console.log('Success:', data);
    })
    .catch((error) => {
        console.error('Error:', error);
    });
}



